def put_player_on_board(board, player):
    '''
    Modifies the game board by placing the player icon at its coordinates.

    Args:
    list: The game board
    dictionary: The player information containing the icon and coordinates

    Returns:
    Nothing
    '''
    if player.level == "Tutorial" or player.level == "Gender" or player.level == 1 or player.level == 2:
        board[1][2] = player.icon
        player.pos_x = 1
        player.pos_y = 2
    elif player.level == 'enchanted_garden':
        board[5][1] = player.icon
        player.pos_x = 5
        player.pos_y = 1
    elif player.level == 'wild_wilds':
        board[2][1] = player.icon
        player.pos_x = 2
        player.pos_y = 1
    elif player.level == 'castle_main_hall':
        board[8][3] = player.icon
        player.pos_x = 8
        player.pos_y = 3
    elif player.level == 'castle_main_hall_2':
        board[6][14] = player.icon
        player.pos_x = 6
        player.pos_y = 14
    elif player.level == "castle_basement":
        board[3][17] = player.icon
        player.pos_x = 3
        player.pos_y = 17
    elif player.level == "tower":
        board[8][1] = player.icon
        player.pos_x = 8
        player.pos_y = 1
    elif player.level == "outro":
        board[8][15] = player.icon
        player.pos_x = 8
        player.pos_y = 15


