    if enemy.name == 'The Big Orb':
        board[8][35] = Enemies.eye.icon
        enemy.pos_x = 8
        enemy.pos_y = 35

    elif enemy.name == "Thunder King":
        board[8][17] = Enemies.eye.icon
        enemy.pos_x = 8
